{"code":"// Synthwave Music Player - React Component\n// Dependencies: React hooks and Lucide icons are provided by reactRuntime.js\n\nfunction App() {\n  const [isPlaying, setIsPlaying] = useState(false);\n  const [progress, setProgress] = useState(0); // 0 to 100\n  const [volume, setVolume] = useState(75);\n  const [currentTime, setCurrentTime] = useState(0);\n  const [duration] = useState(224); // 3:44 in seconds\n  \n  // Visualizer bars state\n  const [bars, setBars] = useState(Array(8).fill(10));\n\n  useEffect(() => {\n    let interval;\n    if (isPlaying) {\n      interval = setInterval(() => {\n        // Update progress\n        setCurrentTime((prev) => {\n          if (prev >= duration) {\n            setIsPlaying(false);\n            return 0;\n          }\n          return prev + 1;\n        });\n\n        // Randomize visualizer bars\n        setBars(bars.map(() => Math.floor(Math.random() * 60) + 20));\n      }, 1000);\n    } else {\n      // Reset visualizer when paused\n      setBars(Array(8).fill(10));\n    }\n    return () => clearInterval(interval);\n  }, [isPlaying, duration, bars]);\n\n  // Update progress percentage based on current time\n  useEffect(() => {\n    setProgress((currentTime / duration) * 100);\n  }, [currentTime, duration]);\n\n  const formatTime = (seconds) => {\n    const mins = Math.floor(seconds / 60);\n    const secs = Math.floor(seconds % 60);\n    return `${mins}:${secs < 10 ? '0' : ''}${secs}`;\n  };\n\n  const handlePlayToggle = () => setIsPlaying(!isPlaying);\n\n  return (\n    React.createElement('div', { className: \"relative w-full h-screen overflow-hidden bg-[#050510] text-cyan-400 font-sans selection:bg-pink-500 selection:text-white\"        ,}\n      /* Import Orbitron Font */\n      , React.createElement('style', null, `\n        @import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;500;700;900&display=swap');\n\n        :root {\n          --neon-pink: #ff00ff;\n          --neon-cyan: #00ffff;\n          --dark-bg: #0a0a1a;\n        }\n\n        body {\n          font-family: 'Orbitron', sans-serif;\n          margin: 0;\n          overflow: hidden;\n        }\n\n        /* Scanlines Effect */\n        .scanlines {\n          background: linear-gradient(\n            to bottom,\n            rgba(255,255,255,0),\n            rgba(255,255,255,0) 50%,\n            rgba(0,0,0,0.2) 50%,\n            rgba(0,0,0,0.2)\n          );\n          background-size: 100% 4px;\n          position: fixed;\n          pointer-events: none;\n          top: 0;\n          left: 0;\n          right: 0;\n          bottom: 0;\n          z-index: 50;\n        }\n        \n        /* CRT Flicker */\n        @keyframes flicker {\n          0% { opacity: 0.97; }\n          5% { opacity: 0.95; }\n          10% { opacity: 0.9; }\n          15% { opacity: 0.95; }\n          20% { opacity: 0.98; }\n          50% { opacity: 0.95; }\n          80% { opacity: 0.9; }\n          100% { opacity: 0.97; }\n        }\n        .crt-flicker {\n          animation: flicker 0.15s infinite;\n        }\n\n        /* Moving Grid Background */\n        .retro-grid {\n          position: absolute;\n          width: 200%;\n          height: 100%;\n          bottom: -50%;\n          left: -50%;\n          background-image: \n            linear-gradient(var(--neon-pink) 1px, transparent 1px),\n            linear-gradient(90deg, var(--neon-pink) 1px, transparent 1px);\n          background-size: 50px 50px;\n          background-position: center bottom;\n          transform: perspective(500px) rotateX(60deg);\n          animation: gridMove 20s linear infinite;\n          opacity: 0.2;\n          box-shadow: 0 0 50px var(--neon-pink);\n        }\n\n        @keyframes gridMove {\n          0% { transform: perspective(500px) rotateX(60deg) translateY(0); }\n          100% { transform: perspective(500px) rotateX(60deg) translateY(50px); }\n        }\n\n        /* Sun / Horizon Glow */\n        .sun {\n          position: absolute;\n          bottom: 20%;\n          left: 50%;\n          transform: translateX(-50%);\n          width: 400px;\n          height: 400px;\n          background: linear-gradient(to top, #ff00ff, #ffcc00);\n          border-radius: 50%;\n          filter: blur(20px);\n          opacity: 0.6;\n          z-index: 0;\n          clip-path: polygon(0 0, 100% 0, 100% 60%, 0 60%);\n        }\n\n        /* Neon Glow Helpers */\n        .text-glow-cyan { text-shadow: 0 0 10px var(--neon-cyan), 0 0 20px var(--neon-cyan); }\n        .text-glow-pink { text-shadow: 0 0 10px var(--neon-pink), 0 0 20px var(--neon-pink); }\n        \n        .box-glow-pink { box-shadow: 0 0 15px var(--neon-pink), inset 0 0 15px var(--neon-pink); }\n        .box-glow-cyan { box-shadow: 0 0 15px var(--neon-cyan), inset 0 0 5px var(--neon-cyan); }\n\n        /* Custom Range Slider */\n        input[type=range] {\n          -webkit-appearance: none;\n          background: transparent;\n        }\n        input[type=range]::-webkit-slider-thumb {\n          -webkit-appearance: none;\n          height: 16px;\n          width: 16px;\n          border-radius: 50%;\n          background: #00ffff;\n          box-shadow: 0 0 10px #00ffff;\n          cursor: pointer;\n          margin-top: -6px;\n        }\n        input[type=range]::-webkit-slider-runnable-track {\n          width: 100%;\n          height: 4px;\n          background: #333;\n          border-radius: 2px;\n        }\n        input[type=range]:focus {\n          outline: none;\n        }\n      `)\n\n      /* Background Environment */\n      , React.createElement('div', { className: \"absolute inset-0 z-0 bg-gradient-to-b from-[#0a0a1a] via-[#1a1a3a] to-[#2a0a2a]\"      ,}\n        , React.createElement('div', { className: \"sun\",})\n        , React.createElement('div', { className: \"retro-grid\",})\n        /* Stars */\n        , React.createElement('div', { className: \"absolute top-0 left-0 w-full h-1/2 opacity-60\"     , \n             style: {backgroundImage: 'radial-gradient(white 1px, transparent 1px)', backgroundSize: '50px 50px'},})\n      )\n\n      /* Main Content Area */\n      , React.createElement('div', { className: \"relative z-10 flex items-center justify-center w-full h-full crt-flicker p-4\"        ,}\n\n        /* Player Card */\n        , React.createElement('div', { className: \"relative w-full max-w-md bg-[#0a0a1a]/90 border-2 border-cyan-400 p-6 rounded-lg backdrop-blur-sm box-glow-cyan\"         ,}\n\n          /* Header */\n          , React.createElement('div', { className: \"flex justify-between items-center mb-6 border-b border-pink-500/30 pb-2\"      ,}\n            , React.createElement('div', { className: \"flex items-center gap-2\"  ,}\n              , React.createElement(Disc, { className: \"w-5 h-5 text-pink-500 animate-spin-slow\"   ,} )\n              , React.createElement('span', { className: \"text-xs uppercase tracking-widest text-pink-400\"   ,}, \"SynthOS Music v1.0\"  )\n            )\n            , React.createElement('div', { className: \"flex gap-2\" ,}\n              , React.createElement(Minimize2, { className: \"w-4 h-4 text-cyan-600 cursor-pointer hover:text-cyan-400\"    ,} )\n              , React.createElement(Maximize2, { className: \"w-4 h-4 text-cyan-600 cursor-pointer hover:text-cyan-400\"    ,} )\n              , React.createElement(X, { className: \"w-4 h-4 text-pink-600 cursor-pointer hover:text-pink-400\"    ,} )\n            )\n          )\n\n          /* Album Art Area */\n          , React.createElement('div', { className: \"relative w-full aspect-square mb-6 group cursor-pointer overflow-hidden rounded-md border-2 border-pink-500 box-glow-pink\"          ,}\n            /* Placeholder Grid Art */\n            , React.createElement('div', { className: \"absolute inset-0 bg-gradient-to-br from-indigo-900 to-purple-900 flex items-center justify-center\"       ,}\n              , React.createElement('div', { className: \"w-full h-full opacity-30\"  , \n                   style: {\n                     backgroundImage: 'linear-gradient(0deg, transparent 24%, rgba(255, 0, 255, .3) 25%, rgba(255, 0, 255, .3) 26%, transparent 27%, transparent 74%, rgba(255, 0, 255, .3) 75%, rgba(255, 0, 255, .3) 76%, transparent 77%, transparent), linear-gradient(90deg, transparent 24%, rgba(255, 0, 255, .3) 25%, rgba(255, 0, 255, .3) 26%, transparent 27%, transparent 74%, rgba(255, 0, 255, .3) 75%, rgba(255, 0, 255, .3) 76%, transparent 77%, transparent)',\n                     backgroundSize: '30px 30px'\n                   },}\n              )\n\n              /* Central vector sun/pyramid art */\n              , React.createElement('div', { className: \"absolute w-32 h-32 bg-gradient-to-t from-yellow-400 to-pink-600 rounded-full blur-md opacity-80\"        ,})\n              , React.createElement('div', { className: \"absolute bottom-0 w-full h-1/2 bg-[#0a0a1a] opacity-50 clip-path-triangle\"      ,})\n\n              , React.createElement('div', { className: \"absolute z-10 text-center\"  ,}\n                 , React.createElement('span', { className: \"block text-4xl font-bold text-white text-glow-cyan tracking-widest\"     ,}, \"RETRO\")\n                 , React.createElement('span', { className: \"block text-2xl font-light text-pink-300 text-glow-pink tracking-[0.5em]\"     ,}, \"WAVE\")\n              )\n            )\n          )\n\n          /* Track Info */\n          , React.createElement('div', { className: \"text-center mb-6 space-y-2\"  ,}\n            , React.createElement('h2', { className: \"text-2xl font-bold text-white tracking-wider text-glow-cyan truncate\"     ,}, \"NIGHTCALL\"\n\n            )\n            , React.createElement('p', { className: \"text-pink-500 text-sm tracking-widest uppercase text-glow-pink\"    ,}, \"Kavinsky\"\n\n            )\n          )\n\n          /* Visualizer */\n          , React.createElement('div', { className: \"flex justify-center items-end h-12 gap-2 mb-6 px-4\"      ,}\n            , bars.map((height, i) => (\n              React.createElement('div', { \n                key: i,\n                className: \"w-3 bg-gradient-to-t from-pink-600 to-cyan-400 rounded-t-sm transition-all duration-100 ease-linear shadow-[0_0_10px_#ff00ff]\"        ,\n                style: { height: `${height}%` },}\n)\n            ))\n          )\n\n          /* Progress Bar */\n          , React.createElement('div', { className: \"mb-2 group\" ,}\n            , React.createElement('input', { \n              type: \"range\", \n              min: \"0\", \n              max: \"100\", \n              value: progress,\n              onChange: (e) => {\n                const newTime = (e.target.value / 100) * duration;\n                setCurrentTime(newTime);\n                setProgress(e.target.value);\n              },\n              className: \"w-full h-1 bg-gray-700 rounded-lg appearance-none cursor-pointer\"     ,\n              style: {\n                background: `linear-gradient(to right, #ff00ff ${progress}%, #1f2937 ${progress}%)`\n              },}\n            )\n          )\n\n          /* Time Display */\n          , React.createElement('div', { className: \"flex justify-between text-xs text-cyan-300 font-mono mb-6\"     ,}\n            , React.createElement('span', null, formatTime(currentTime))\n            , React.createElement('span', null, formatTime(duration))\n          )\n\n          /* Controls */\n          , React.createElement('div', { className: \"flex justify-between items-center mb-6\"   ,}\n            , React.createElement('button', { className: \"text-cyan-600 hover:text-cyan-300 transition-colors\"  ,}\n               , React.createElement(Volume2, { size: 20,} )\n            )\n\n            , React.createElement('div', { className: \"flex items-center gap-6\"  ,}\n              , React.createElement('button', { \n                className: \"text-pink-600 hover:text-pink-400 transition-all hover:scale-110\"   ,\n                onClick: () => setCurrentTime(Math.max(0, currentTime - 10)),}\n\n                , React.createElement(SkipBack, { size: 28,} )\n              )\n\n              , React.createElement('button', { \n                onClick: handlePlayToggle,\n                className: `w-14 h-14 rounded-full flex items-center justify-center border-2 border-cyan-400 text-white shadow-[0_0_15px_#00ffff] hover:bg-cyan-900/30 transition-all hover:scale-105 ${isPlaying ? 'bg-cyan-400/10' : ''}`,}\n\n                , isPlaying ? React.createElement(Pause, { size: 24, fill: \"currentColor\",} ) : React.createElement(Play, { size: 24, fill: \"currentColor\", className: \"ml-1\",} )\n              )\n\n              , React.createElement('button', { \n                className: \"text-pink-600 hover:text-pink-400 transition-all hover:scale-110\"   ,\n                onClick: () => setCurrentTime(Math.min(duration, currentTime + 10)),}\n\n                , React.createElement(SkipForward, { size: 28,} )\n              )\n            )\n\n            , React.createElement('div', { className: \"relative w-20 group flex items-center\"    ,}\n               , React.createElement(Volume1, { size: 20, className: \"text-cyan-600 mr-2\" ,} )\n               , React.createElement('input', { \n                 type: \"range\", \n                 min: \"0\", \n                 max: \"100\", \n                 value: volume,\n                 onChange: (e) => setVolume(e.target.value),\n                 className: \"w-full h-1 rounded-lg cursor-pointer\"   ,\n                 style: {\n                   background: `linear-gradient(to right, #00ffff ${volume}%, #1f2937 ${volume}%)`,\n                   height: '2px'\n                 },}\n               )\n            )\n          )\n        )\n      )\n\n      /* CRT Scanline Overlay */\n      , React.createElement('div', { className: \"scanlines\",})\n\n      /* Vignette */\n      , React.createElement('div', { className: \"fixed inset-0 pointer-events-none z-40 bg-[radial-gradient(circle_at_center,transparent_50%,rgba(0,0,0,0.6)_100%)]\"    ,})\n\n    )\n  );\n}","componentName":"App","lucideIcons":[],"mode":"react"}