  cursor: pointer;
  box-shadow:
    0 4px 16px rgba(102, 126, 234, 0.4),
    inset 0 -2px 8px rgba(0, 0, 0, 0.15),
    inset 0 2px 8px rgba(255, 255, 255, 0.3);
  transition: all 0.3s ease;
}

.y2k-chrome-button:hover {
